mapper.insert:

	select SEQ_RM.NEXTVAL FROM DUAL

INSERT INTO READINGROOM VALUES(
#{rm_code}, #{rm_lcode}, #{rm_name}, #{rm_low}, #{rm_col}
)

mapper.delete:
	{call
        declare
        begin
            delete from READINGROOM where rm_code = #{rm_code};
            delete from seats where se_code = #{rm_code};
        end
    }
